def initialize(h=134,w=51):#initialize kittypy console
    os.system("mode con cols="+str(h))
    os.system("mode con lines="+str(w))
    try:
        height = os.get_terminal_size().lines - 3
        width = os.get_terminal_size().columns - 2
    except:
        height=100
        width=100

def print_center(s,cp):#internal
    print(' ' * ((cp - len(s))//2) + s)

def get_ansi_color_code(r, g, b):#internal
    if r == g and g == b:
        if r < 8:
            return 16
        if r > 248:
            return 231
        return round(((r - 8) / 247) * 24) + 232
    return 16 + (36 * round(r / 255 * 5)) + (6 * round(g / 255 * 5)) + round(b / 255 * 5)


def get_color(r, g, b):#internal
    return "\x1b[48;5;{}m \x1b[0m".format(int(get_ansi_color_code(r,g,b)))

def to_gray(r, g, b):
    gs = r/3 + g/3 + b/3
    if gs >=0 and gs <=40:
        return " "
    elif gs >40 and gs <= 90:
        return "░"
    elif gs >90 and gs <= 150:
        return "▒"
    elif gs > 150 and gs <=200:
        return "▓"
    elif gs > 200 and gs <= 255:
        return "█"


def add_scene(scn,typ="img",res=Image.ANTIALIAS):#add scene
    if typ=="img":
        mscn=[]
        try:
            img = Image.open(scn)
        except FileNotFoundError:
            exit('Image not found.')
        h = height
        w = int((img.width / img.height) * h)
        h= h//2
        img = img.resize((w,h), res)
        img_arr = np.asarray(img)
        h,w,c = img_arr.shape
        for x in range(h):
            tim=""
            for y in range(w):
                pix = img_arr[x][y]
                tim += str(to_gray(pix[0], pix[1], pix[2]))
            mscn.append(tim)
        scenes.append(mscn)
    elif typ=="lst":
        mscn=[]
        for i in scn:
            mscn.append(i)
        scenes.append(mscn)
    fg.clear()
    for bb in range(0,len(scenes)):
        fg.append([])
        for i in scenes[bb]:
            X=""
            X = str(i)
            fg[bb].append(X)


def teleport(xpt):
    location[0],location[1] = xpt[0],xpt[1]
                
def draw_frame(txtargs=""):#goto position and show scene
    os.system("cls")
    fg.clear()
    currentframe.clear()
    if txtargs != "":
        print(txtargs)
    for bb in range(0,len(scenes)):
        fg.append([])
        for i in scenes[bb]:
            X=""
            X = str(i)
            fg[bb].append(X)
    for y in range(0,len(sprites[cursp])):
        fg[curscn][location[1]+y] = scenes[curscn][location[1]+y][0:location[0]] + chcolor + sprites[cursp][y] + backfore + backback + scenes[curscn][location[1]+y][location[0] + len(sprites[cursp][y]):]
    for k in fg[curscn]:
        print(backback + backfore + k + Style.RESET_ALL)
        currentframe.append(backback + backfore + k + Style.RESET_ALL)
        
def set_scene(idx=0):#set scene
    curscn=idx
    draw_frame()

def show_message(msg,slp=3):#show a message - useful for game over
    os.system("cls")
    k=msg.split("\n")
    for g in k:
        print_center("               " + g,100)
    sleep(slp)

def add_character(cha,typ="img",res=Image.ANTIALIAS):#add a character into the game
    if typ=="img":
        mchr=[]
        try:
            img = Image.open(scn)
        except FileNotFoundError:
            exit('Image not found.')
        h = height
        w = int((img.width / img.height) * h)
        img = img.resize((w,h), res)
        img_arr = np.asarray(img)
        h,w,c = img_arr.shape
        for x in range(h):
            tim=""
            for y in range(w):
                pix = img_arr[x][y]
                tim += str(to_gray(pix[0], pix[1], pix[2]))
            mchr.append(tim)
        sprites.append(mchr)
    elif typ=="lst":
        mchr=[]
        for i in cha:
            mchr.append(i)
        sprites.append(mchr)

def set_character(idx):#switch character sprite
    cursp=idx
    draw_frame()

def full_restart():#fully restart the game. useful after game over
    os.system('python nullvoid.py')
    exit()
    
def touching(typ,arg):#detect collision of sprite with a char, point edge or box
    tch=False
    coords=[]
    for q in range(0,len(sprites[cursp][0])+2):
        for b in range(0,len(sprites[cursp])+2):
            coords.append([location[0]+q-1,location[1]+b-1])
    cds = [item for item in coords if item[0] >= 0 or item[0] <= len(scenes[curscn][0]) or item[1] <= len(scenes[curscn]) or item[1] >= 0]
    if typ=="char":
        for i in cds:
            if scenes[curscn][i[1]][i[0]] == arg:
                tch=True
                break
    elif typ=="point":
        if arg in cds:
            tch=True
    elif typ=="edge":
        if arg=="rt" or arg=="right":
            for z in cds:
                if z[0] == len(scenes[curscn][0]):
                    tch=True
                    break
        elif arg=="lt" or arg=="left":
            for z in cds:
                if z[0] == 0:
                    tch=True
                    break
        elif arg=="tp" or arg=="top":
            for z in cds:
                if z[1] == 0:
                    tch=True
                    break
        elif arg=="btm" or arg=="bottom":
            for z in cds:
                if z[1] == len(scenes[curscn]):
                    tch=True
                    break
    elif typ=="box":
        for tt in cds:
            if tt in arg:
                tch==True
    return tch

def distance_to(arg):#gets the distance of closest point of sprite to specified point
    coords=[]
    for q in range(0,len(sprites[cursp][0])+1):
        for b in range(0,len(sprites[cursp])+1):
            coords.append([location[0]+q,location[1]+b])
    cds = [item for item in coords if item[0] >= 0 or item[0] <= len(scenes[curscn][0]) or item[1] <= len(scenes[curscn]) or item[1] >= 0]
    st=[]
    for pt in cds:
        st.append(math.sqrt((pt[0]-arg[0])**2 + (pt[1] - arg[1])**2))
    return math.floor(min(st))

def await_get_input(istt="Enter Command> "):
    print(istt,end="")
    input_char = msvcrt.getch()
    return str(input_char)[2:3]

def wait(tm):
    sleep(tm)

def wrap_scene(typ="right",arg=5):#moves the background to up, down, left or right, while wrapping it around.
    if typ=="right":
        for i in range(0,len(scenes[curscn])):
            scenes[curscn][i] = scenes[curscn][i][arg:] + scenes[curscn][i][0:arg]
    elif typ=="left":
        for i in range(0,len(scenes[curscn])):
            scenes[curscn][i] = scenes[curscn][i][len(scenes[curscn][i])-arg:] + scenes[curscn][i][0:len(scenes[curscn][i])-arg]
    elif typ=="up":
        print("to be implemented")
    elif typ=="down":
        print("to be implemented")
    draw_frame()
    
def move_to(point,speed=100):#movement
    dist=distance_to(point)
    ptss=[]
    for i in range(1,dist+1):
        ptss.append([location[0] + int(i/dist * (location[0]-point[0])), location[1] + int(i/dist * (location[1]-point[1]))])
    ptss.append(point)
    for b in ptss:
        teleport(b)
        draw_frame()
        wait(1/speed)
    location[0],location[1] = point[0],point[1]

def show_splash(scn,dly=1,res=Image.ANTIALIAS):#show the splash screen
    try:
        img = Image.open(scn)
    except FileNotFoundError:
        exit('Image not found.')
    h = height
    w = int((img.width / img.height) * h)
    h = h//2
    img = img.resize((w,h), res)
    img_arr = np.asarray(img)
    h,w,c = img_arr.shape
    for x in range(h):
        tim=""
        for y in range(w):
            pix = img_arr[x][y]
            tim += str((get_color(pix[0], pix[1], pix[2])))
        print(tim)
    wait(dly)
    os.system("cls")

