def gty(wt,cj):
    while location[1] <= width - len(sprites[cursp]) and cj:
        try:
            teleport([location[0],location[1]+1])
            wait(wt)
        except:
            print("err")

def gravity(spd=2,cj=True):
    oth = Thread(target=gty, args=(1/spd,cj,))
    oth.start()

